mapper.select:
SELECT * FROM bill_sys.user_bill
WHERE user_id = ${vo.userId}
AND date BETWEEN ${vo.startDate} AND ${vo.endDate}
ORDER BY date DESC
LIMIT ${vo.page} ,${vo.limit}

SELECT * FROM bill_sys.user_bill
WHERE user_id = ${vo.userId}
AND account = ${vo.account}
ORDER BY date DESC
LIMIT ${vo.page} ,${vo.limit}
