mapper.select:
SELECT user_id, username, password, whether_it_is_disabled_or_not
FROM bill_sys.user
WHERE user_id = #{userId}

SELECT user_id, username, password
FROM bill_sys.user
WHERE username = #{username}
AND password = #{password}

SELECT user_id, account, balance
FROM bill_sys."user_account"
WHERE user_id = #{userId}

mapper.update:
UPDATE bill_sys."user_account"
SET balance = #{balance}
WHERE user_id = #{userId}
