mapper.select:
SELECT id, record_enum, amount, date
FROM bill_sys.user_bill
WHERE user_id = #{userId}
AND date BETWEEN #{vo.startDate} AND #{vo.endDate}
ORDER BY date DESC
LIMIT #{vo.page} ,#{vo.limit}

SELECT id, record_enum, amount, date
FROM bill_sys.user_bill
WHERE user_id = #{userId}
AND account = #{vo.account}
ORDER BY date DESC
LIMIT #{vo.page} ,#{vo.limit}

SELECT id, record_enum, amount, date
FROM bill_sys.user_bill
WHERE user_id = #{userId}
AND amount BETWEEN #{vo.startAmount} AND #{vo.endAmount}
ORDER BY date DESC
LIMIT #{vo.page} ,#{vo.limit}

SELECT id, record_enum, amount, date
FROM bill_sys.user_bill
WHERE user_id = #{userId}
AND record_enum = #{vo.usageEnum}
ORDER BY date DESC
LIMIT #{vo.page} ,#{vo.limit}

SELECT id, record_enum, amount, date
FROM bill_sys.user_bill
WHERE user_id = #{userId}
AND (remarks LIKE '%#{vo.keyword}%')
ORDER BY date DESC
LIMIT #{vo.page} ,#{vo.limit}
